
public class SeriesUp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("value-->"+seriesUp(3));

	}

	
	public static int[] seriesUp(int n) {
		  int[] nums = null;
		  for(int i=0;i<n;i++){
		    nums[i]=i;
		  }
		return nums.toString();
		}

}
